using System;\nusing System.Diagnostics;\n\npublic class Example\n{\n    public static void Main()\n    {\n        Process process = null;\n        try\n        {\n            process = new Process();\n            process.StartInfo.FileName = "example.exe";\n            process.Start();\n\n            // Wait for the process to exit if needed\n            process.WaitForExit();\n        }\n        finally\n        {\n            if (process != null)\n            {\n                process.Close();\n            }\n        }\n    }\n}