public class AtLeastOneSelectedItemValidationRule : ValidationRule\n{\n    public override ValidationResult Validate(object value, CultureInfo cultureInfo)\n    {\n        if (value is IList selectedItems && selectedItems.Count > 0)\n        {\n            return ValidationResult.ValidResult;\n        }\n        return new ValidationResult(false, "At least one item must be selected.");\n    }\n}