public class NHibernateValidatorInitializer\n   {\n       public static void Initialize()\n       {\n           var nhvConfiguration = new NHibernate.Validator.Cfg.Loquacious.FluentConfiguration();\n           nhvConfiguration\n               .Register(Assembly.Load("YourAssemblyWithValidators"))\n               .SetDefaultValidatorMode(ValidatorMode.UseExternal)\n               .IntegrateWithNHibernate\n               .ApplyingDDLConstraints()\n               .And\n               .RegisteringListeners();\n\n           NHibernate.Validator.Cfg.Environment.SharedEngineProvider = new NHibernate.Validator.Engine.SharedEngineProvider();\n           NHibernate.Validator.Cfg.Environment.SharedEngineProvider.Configure(nhvConfiguration);\n       }\n   }