using System;\nusing System.ServiceProcess;\n\nclass Program\n{\n    static void Main()\n    {\n        ServiceController sc = new ServiceController("W3SVC");\n\n        try\n        {\n            if (sc.Status == ServiceControllerStatus.Running)\n            {\n                Console.WriteLine("IIS is enabled and running.");\n            }\n            else\n            {\n                Console.WriteLine("IIS is installed but not running.");\n            }\n        }\n        catch (InvalidOperationException)\n        {\n            Console.WriteLine("IIS is not installed.");\n        }\n    }\n}