using Microsoft.Win32;\nusing System;\n\npublic class ComChecker\n{\n    public static bool IsComDllRegistered(string clsid)\n    {\n        string registryKey = $@"CLSID\{clsid}";\n        using (RegistryKey key = Registry.ClassesRoot.OpenSubKey(registryKey))\n        {\n            return key != null;\n        }\n    }\n\n    public static void Main(string[] args)\n    {\n        string clsid = "{Your-CLSID-Here}"; // Replace with the actual CLSID of the COM component\n        bool isRegistered = IsComDllRegistered(clsid);\n\n        if (isRegistered)\n        {\n            Console.WriteLine($"COM DLL with CLSID {clsid} is registered.");\n        }\n        else\n        {\n            Console.WriteLine($"COM DLL with CLSID {clsid} is not registered.");\n        }\n    }\n}