using System;\nusing System.IO;\nusing System.Text;\n\npublic class Program\n{\n    public static void Main()\n    {\n        // Example byte array with BOM\n        byte[] i_fileBytes = File.ReadAllBytes("path_to_your_file");\n\n        // Use StreamReader to detect BOM and read the text\n        string unicodeString;\n        using (var stream = new MemoryStream(i_fileBytes))\n        using (var reader = new StreamReader(stream, Encoding.UTF8, true)) // UTF8 with BOM detection\n        {\n            unicodeString = reader.ReadToEnd();\n        }\n\n        // Convert the string to ASCII, replacing non-ASCII characters with '?'\n        byte[] ansiBytes = Encoding.ASCII.GetBytes(unicodeString);\n        string ansiString = Encoding.ASCII.GetString(ansiBytes);\n\n        Console.WriteLine(ansiString);\n    }\n}