private void OnCreated(object source, FileSystemEventArgs e)\n{\n    string filePath = e.FullPath;\n    int timeoutMilliseconds = 10000; // Set your desired timeout period\n\n    if (FileAccessHelper.WaitForFile(filePath, timeoutMilliseconds))\n    {\n        // The file is accessible, you can now read or rename it\n    }\n    else\n    {\n        // Handle the timeout scenario\n        Console.WriteLine("The file is still locked after waiting for the specified timeout period.");\n    }\n}