using System;\nusing System.Collections.Generic;\n\nclass Program\n{\n    static void Main()\n    {\n        // Initialize the HashSet with the long numbers\n        HashSet<long> numbers = new HashSet<long>();\n\n        // Add numbers to the HashSet\n        for (long i = 0; i < 1000000; i++)\n        {\n            numbers.Add(i);\n        }\n        \n        // Check if a specific number is in the HashSet\n        long numberToFind = 999999;\n        if (numbers.Contains(numberToFind))\n        {\n            Console.WriteLine($"{numberToFind} is in the collection.");\n        }\n        else\n        {\n            Console.WriteLine($"{numberToFind} is not in the collection.");\n        }\n    }\n}