using System.Diagnostics;\n\npublic void StopIIS(string serverName)\n{\n    Process proc = new Process();\n    proc.EnableRaisingEvents = false;\n    proc.StartInfo.WindowStyle = ProcessWindowStyle.Hidden;\n    proc.StartInfo.FileName = "iisreset";\n    proc.StartInfo.Arguments = $"{serverName} /stop";\n    proc.Start();\n    proc.WaitForExit();\n}\n\npublic void StartIIS(string serverName)\n{\n    Process proc = new Process();\n    proc.EnableRaisingEvents = false;\n    proc.StartInfo.WindowStyle = ProcessWindowStyle.Hidden;\n    proc.StartInfo.FileName = "iisreset";\n    proc.StartInfo.Arguments = $"{serverName} /start";\n    proc.Start();\n    proc.WaitForExit();\n}