using System;\nusing System.Collections.Generic;\n\npublic class Range\n{\n    public int Start { get; set; }\n    public int End { get; set; }\n\n    public Range(int start, int end)\n    {\n        Start = start;\n        End = end;\n    }\n\n    public bool Contains(int value)\n    {\n        return value >= Start && value <= End;\n    }\n}\n\npublic class RangeChecker\n{\n    private List<Range> ranges;\n\n    public RangeChecker()\n    {\n        ranges = new List<Range>();\n    }\n\n    public void LoadRangesFromDatabase()\n    {\n        // Load ranges from the database (pseudo-code)\n        ranges.Add(new Range(1000, 20400));\n        ranges.Add(new Range(22200, 24444));\n        // Add more ranges as needed\n    }\n\n    public bool IsValueInRange(int value)\n    {\n        foreach (var range in ranges)\n        {\n            if (range.Contains(value))\n            {\n                return true;\n            }\n        }\n        return false;\n    }\n}\n\npublic class Program\n{\n    public static void Main(string[] args)\n    {\n        RangeChecker rangeChecker = new RangeChecker();\n        rangeChecker.LoadRangesFromDatabase();\n\n        int valueToCheck = 15000;\n        bool isInRange = rangeChecker.IsValueInRange(valueToCheck);\n\n        Console.WriteLine($"Is {valueToCheck} in any range? {isInRange}");\n    }\n}