using System;\nusing System.Data.SqlClient;\n\nclass Program\n{\n    static void Main()\n    {\n        string connectionString = "your_connection_string_here";\n        string terminalId = "your_terminal_id_here";\n\n        using (SqlConnection connection = new SqlConnection(connectionString))\n        {\n            connection.Open();\n\n            using (SqlTransaction transaction = connection.BeginTransaction())\n            {\n                try\n                {\n                    // Lock the row corresponding to the terminal\n                    string lockQuery = "SELECT * FROM TerminalTable WITH (UPDLOCK, ROWLOCK) WHERE TerminalID = @TerminalID";\n\n                    using (SqlCommand command = new SqlCommand(lockQuery, connection, transaction))\n                    {\n                        command.Parameters.AddWithValue("@TerminalID", terminalId);\n                        using (SqlDataReader reader = command.ExecuteReader())\n                        {\n                            if (reader.Read())\n                            {\n                                // Row is now locked\n                                Console.WriteLine("Row locked for terminal: " + terminalId);\n                            }\n                        }\n                    }\n\n                    // Keep the transaction open during the session\n                    Console.WriteLine("Press Enter to end session and release lock...");\n                    Console.ReadLine();\n\n                    transaction.Commit();\n                }\n                catch (Exception ex)\n                {\n                    Console.WriteLine("Error: " + ex.Message);\n                    transaction.Rollback();\n                }\n            }\n        }\n    }\n}