public override string GenerateCreateTable(TableSchema.Table table)\n{\n    StringBuilder sql = new StringBuilder();\n    sql.AppendLine($"CREATE TABLE {table.Name} (");\n\n    foreach (TableSchema.TableColumn col in table.Columns)\n    {\n        sql.AppendLine($"{col.ColumnName} {GetDbType(col)}{(col.IsNullable ? " NULL" : " NOT NULL")},");\n    }\n\n    // Remove the last comma\n    if (table.Columns.Count > 0)\n    {\n        sql.Length -= 1;\n    }\n\n    sql.AppendLine(")");\n\n    // Add primary key constraint if specified\n    if (table.PrimaryKey != null)\n    {\n        sql.AppendLine($"CONSTRAINT {table.PrimaryKey.Name} PRIMARY KEY ({table.PrimaryKey.ColumnName})");\n    }\n\n    return sql.ToString();\n}