using System;\nusing Oracle.ManagedDataAccess.Client;\n\nnamespace OracleStoredProcedureExample\n{\n    class Program\n    {\n        static void Main(string[] args)\n        {\n            string connectionString = "User Id=dev;Password=yourpassword;Data Source=yourdatasource";\n\n            using (OracleConnection conn = new OracleConnection(connectionString))\n            {\n                conn.Open();\n\n                // Example call to PACKAGE1.FUNC1\n                int result = CallFunc1(conn, 12, 34, "hello");\n                Console.WriteLine("Result: " + result);\n            }\n        }\n\n        static int CallFunc1(OracleConnection conn, int param1, int param2, string param3)\n        {\n            using (OracleCommand cmd = new OracleCommand("PACKAGE1.FUNC1", conn))\n            {\n                cmd.CommandType = System.Data.CommandType.StoredProcedure;\n\n                // Define parameters\n                cmd.Parameters.Add("p1", OracleDbType.Int32).Value = param1;\n                cmd.Parameters.Add("p2", OracleDbType.Int32).Value = param2;\n                cmd.Parameters.Add("p3", OracleDbType.Varchar2).Value = param3;\n                cmd.Parameters.Add("result", OracleDbType.Int32).Direction = System.Data.ParameterDirection.ReturnValue;\n\n                // Execute the stored procedure\n                cmd.ExecuteNonQuery();\n\n                // Retrieve the result\n                return Convert.ToInt32(cmd.Parameters["result"].Value);\n            }\n        }\n    }\n}