public class Parent\n{\n    public int ID { get; set; }\n    public Dictionary<int, string> Childs { get; set; }\n}\n\npublic class Child\n{\n    public int ParentID { get; set; }\n    public int ID { get; set; }\n    public string Name { get; set; }\n}\n\npublic List<Parent> GetParentsWithChildren()\n{\n    using (var context = new YourDataContext())\n    {\n        var query = from parent in context.Parents\n                    select new Parent\n                    {\n                        ID = parent.ID,\n                        Childs = context.Children\n                                    .Where(child => child.ParentID == parent.ID)\n                                    .ToDictionary(child => child.ID, child => child.Name)\n                    };\n\n        return query.ToList();\n    }\n}