using System.Net;\nusing System.Net.Mail;\n\npublic void SendEmailToDistributionGroup()\n{\n    MailMessage mail = new MailMessage();\n    mail.From = new MailAddress("steveb@microsoft.com");\n    mail.To.Add("distributiongroup@example.com"); // Use the email address of the AD distribution group\n\n    // Set the content\n    mail.Subject = "This is an email";\n    mail.Body = "This is a sample body with HTML in it.";\n    mail.IsBodyHtml = true;\n\n    // Configure the SMTP client\n    SmtpClient smtp = new SmtpClient("smtp.example.com"); // Use your SMTP server address\n    smtp.Port = 587; // Common port for SMTP\n    smtp.Credentials = new NetworkCredential("username", "password"); // Use your SMTP credentials\n    smtp.EnableSsl = true; // Enable SSL for secure transmission\n\n    try\n    {\n        // Send the message\n        smtp.Send(mail);\n        Console.WriteLine("Email sent successfully.");\n    }\n    catch (SmtpException ex)\n    {\n        Console.WriteLine("Error sending email: " + ex.Message);\n    }\n}