using System;\nusing System.Security.Cryptography;\nusing System.Text;\n\npublic class RSAKeyExport\n{\n    public static void Main()\n    {\n        using (var rsa = new RSACryptoServiceProvider(2048))\n        {\n            // Export the public key\n            var publicKey = rsa.ExportParameters(false);\n            var publicKeyString = ExportPublicKeyToPEM(publicKey);\n            System.IO.File.WriteAllText("publicKey.pem", publicKeyString);\n        }\n    }\n\n    private static string ExportPublicKeyToPEM(RSAParameters publicKey)\n    {\n        using (var sw = new System.IO.StringWriter())\n        {\n            var pemWriter = new PemWriter(sw);\n            var keyParameter = DotNetUtilities.GetRsaPublicKey(publicKey);\n            pemWriter.WriteObject(keyParameter);\n            return sw.ToString();\n        }\n    }\n}