using System;\nusing System.Windows.Forms;\n\npublic class MyForm : Form\n{\n    public MyForm()\n    {\n        InitializeComponent();\n    }\n\n    // Override the ProcessCmdKey method\n    protected override bool ProcessCmdKey(ref Message msg, Keys keyData)\n    {\n        // Check if the key is the Enter key\n        if (keyData == Keys.Enter)\n        {\n            // Handle the Enter key here\n            // For example, you can perform a custom action or simply return true to suppress the default behavior\n            return true;\n        }\n\n        // Call the base class method to process other keys normally\n        return base.ProcessCmdKey(ref msg, keyData);\n    }\n\n    private void InitializeComponent()\n    {\n        // Initialize your form and controls here\n    }\n\n    // Entry point for the application\n    [STAThread]\n    static void Main()\n    {\n        Application.EnableVisualStyles();\n        Application.SetCompatibleTextRenderingDefault(false);\n        Application.Run(new MyForm());\n    }\n}