try\n   {\n       // Attempt to fill the DataSet\n       yourDataAdapter.Fill(yourDataSet);\n   }\n   catch (ConstraintException ex)\n   {\n       // Inspect each table in the DataSet for errors\n       foreach (DataTable table in yourDataSet.Tables)\n       {\n           foreach (DataRow row in table.GetErrors())\n           {\n               // Log or inspect the error\n               Console.WriteLine($"Error in Table: {table.TableName}, Row: {row}");\n               foreach (DataColumn column in table.Columns)\n               {\n                   if (row.HasErrors)\n                   {\n                       Console.WriteLine($"Column {column.ColumnName}: {row.GetColumnError(column)}");\n                   }\n               }\n           }\n       }\n   }