using System;\nusing System.Drawing;\nusing System.Drawing.Drawing2D;\nusing System.Windows.Forms;\n\npublic class RoundedRectangleForm : Form\n{\n    private PictureBox pictureBox;\n\n    public RoundedRectangleForm()\n    {\n        this.pictureBox = new PictureBox\n        {\n            Width = 200,\n            Height = 100,\n            Visible = false // Initially hidden\n        };\n        this.Controls.Add(pictureBox);\n\n        // Create the rounded rectangle and assign it as the content of the PictureBox\n        CreateRoundedRectangle();\n    }\n\n    private void CreateRoundedRectangle()\n    {\n        Bitmap bitmap = new Bitmap(pictureBox.Width, pictureBox.Height);\n        using (Graphics g = Graphics.FromImage(bitmap))\n        {\n            g.SmoothingMode = SmoothingMode.AntiAlias;\n            using (Brush brush = new SolidBrush(Color.Blue))\n            {\n                GraphicsPath path = new GraphicsPath();\n                int radius = 20;\n                path.AddArc(0, 0, radius, radius, 180, 90);\n                path.AddArc(pictureBox.Width - radius, 0, radius, radius, 270, 90);\n                path.AddArc(pictureBox.Width - radius, pictureBox.Height - radius, radius, radius, 0, 90);\n                path.AddArc(0, pictureBox.Height - radius, radius, radius, 90, 90);\n                path.CloseFigure();\n\n                g.FillPath(brush, path);\n            }\n        }\n        pictureBox.Image = bitmap;\n        pictureBox.Visible = true; // Show the PictureBox with the rounded rectangle\n    }\n\n    [STAThread]\n    static void Main()\n    {\n        Application.EnableVisualStyles();\n        Application.Run(new RoundedRectangleForm());\n    }\n}