using System.Diagnostics;\n\npublic class MyWebService : System.Web.Services.WebService\n{\n    private const string APPLICATIONNAME = "MyApp";\n    private const string EVENTLOGNAME = "MyLog";\n\n    [WebMethod]\n    public void MyWebMethod()\n    {\n        WriteToEventLog("Web service method called", EventLogEntryType.Information);\n    }\n\n    private void WriteToEventLog(string message, EventLogEntryType entryType)\n    {\n        if (!EventLog.SourceExists(APPLICATIONNAME))\n        {\n            EventLog.CreateEventSource(APPLICATIONNAME, EVENTLOGNAME);\n        }\n        EventLog.WriteEntry(APPLICATIONNAME, message, entryType);\n    }\n}