using System;\nusing System.Data;\n\npublic class Program\n{\n    public static void Main()\n    {\n        // Create and populate the original dataset\n        DataSet originalDataSet = new DataSet();\n        DataTable originalTable = new DataTable("OriginalTable");\n        \n        // Add 28 columns to the original table\n        for (int i = 0; i < 28; i++)\n        {\n            originalTable.Columns.Add("Column" + (i + 1), typeof(string));\n        }\n        \n        // Add the original table to the original dataset\n        originalDataSet.Tables.Add(originalTable);\n\n        // Create a new dataset to hold the subsets\n        DataSet newDataSet = new DataSet();\n        \n        // Create two new tables with 14 columns each\n        DataTable table1 = new DataTable("Table1");\n        DataTable table2 = new DataTable("Table2");\n        \n        // Add the first 14 columns to table1\n        for (int i = 0; i < 14; i++)\n        {\n            table1.Columns.Add(originalTable.Columns[i].ColumnName, originalTable.Columns[i].DataType);\n        }\n        \n        // Add the next 14 columns to table2\n        for (int i = 14; i < 28; i++)\n        {\n            table2.Columns.Add(originalTable.Columns[i].ColumnName, originalTable.Columns[i].DataType);\n        }\n        \n        // Add the new tables to the new dataset\n        newDataSet.Tables.Add(table1);\n        newDataSet.Tables.Add(table2);\n        \n        // Optionally copy rows from the original table to the new tables\n        foreach (DataRow row in originalTable.Rows)\n        {\n            DataRow newRow1 = table1.NewRow();\n            DataRow newRow2 = table2.NewRow();\n            \n            for (int i = 0; i < 14; i++)\n            {\n                newRow1[i] = row[i];\n            }\n            \n            for (int i = 14; i < 28; i++)\n            {\n                newRow2[i - 14] = row[i];\n            }\n            \n            table1.Rows.Add(newRow1);\n            table2.Rows.Add(newRow2);\n        }\n        \n        // newDataSet now contains two tables with 14 columns each\n        Console.WriteLine("New dataset created with two tables having 14 columns each.");\n    }\n}