var query = from person in context.Persons\n            join logEntry in context.LogEntries\n            on person.Key equals logEntry.PersonKey\n            where logEntry.EntryTime == (\n                from le in context.LogEntries\n                where le.PersonKey == person.Key\n                select le.EntryTime).Max()\n            select new\n            {\n                person.Key,\n                person.Name,\n                person.LastName,\n                logEntry.Log,\n                logEntry.EntryTime\n            };