using System;\nusing Microsoft.CodeAnalysis.CSharp.Scripting;\nusing Microsoft.CodeAnalysis.Scripting;\n\nclass Program\n{\n    static async Task Main(string[] args)\n    {\n        while (true)\n        {\n            Console.Write(">");\n            var input = Console.ReadLine();\n\n            if (string.IsNullOrEmpty(input))\n                continue;\n\n            if (input.ToLower() == "exit")\n                break;\n\n            try\n            {\n                var result = await CSharpScript.EvaluateAsync<object>(input, ScriptOptions.Default.WithReferences(AppDomain.CurrentDomain.GetAssemblies()).WithImports("System", "System.Linq", "System.Collections.Generic"));\n                Console.WriteLine(result?.ToString() ?? "null");\n            }\n            catch (Exception ex)\n            {\n                Console.WriteLine($"Error: {ex.Message}");\n            }\n        }\n    }\n}