// Example of a Ranorex script in C#\n    using Ranorex;\n    using Ranorex.Core;\n    using Ranorex.Core.Testing;\n\n    public class SiebelTest {\n        public static void Main(string[] args) {\n            Host.Local.OpenBrowser("http://your-siebel-url", "IE");\n            var username = "/dom[@caption='Siebel']/input[@id='s_swepi_1']";\n            var password = "/dom[@caption='Siebel']/input[@id='s_swepi_2']";\n            var loginButton = "/dom[@caption='Siebel']/button[@id='s_swepi_22']";\n\n            Keyboard.Press("{LControlKey down}");\n            Keyboard.Press("{VKey}");\n            Keyboard.Press("{LControlKey up}");\n\n            Report.Info("Sample message: Entering username");\n            username.As<InputTag>().PressKeys("your-username");\n            password.As<InputTag>().PressKeys("your-password");\n            loginButton.As<ButtonTag>().Click();\n        }\n    }