try\n{\n    string filePath = Server.MapPath("~/App_Data/log.txt"); // Adjust path as necessary\n    string content = "This is a log entry.";\n\n    // Ensure the directory exists\n    var directory = Path.GetDirectoryName(filePath);\n    if (!Directory.Exists(directory))\n    {\n        Directory.CreateDirectory(directory);\n    }\n\n    // Write to the file\n    using (StreamWriter writer = new StreamWriter(filePath, true))\n    {\n        writer.WriteLine(content);\n    }\n}\ncatch (Exception ex)\n{\n    // Handle any exceptions here\n    // For example, log the exception message to an alternative logging mechanism\n    throw new Exception("File writing failed.", ex);\n}