private static readonly object locker = new object();\n\npublic SomeData GetData(string cacheKey)\n{\n    var cachedData = HttpRuntime.Cache[cacheKey] as SomeData;\n    if (cachedData == null)\n    {\n        lock (locker)\n        {\n            cachedData = HttpRuntime.Cache[cacheKey] as SomeData;\n            if (cachedData == null)\n            {\n                cachedData = GetSomeDataToCache();\n                HttpRuntime.Cache.Insert(cacheKey, cachedData, null, DateTime.Today.AddDays(1), Cache.NoSlidingExpiration);\n            }\n        }\n    }\n    return cachedData.Copy();\n}