using System;\nusing System.Collections.Generic;\n\npublic class MyMath\n{\n    public static int MostFreq(int[] _M, out int x)\n    {\n        Dictionary<int, int> frequencyMap = new Dictionary<int, int>();\n        int maxFrequency = 0;\n        x = _M[0];\n\n        foreach (int number in _M)\n        {\n            if (frequencyMap.ContainsKey(number))\n            {\n                frequencyMap[number]++;\n            }\n            else\n            {\n                frequencyMap[number] = 1;\n            }\n\n            if (frequencyMap[number] > maxFrequency)\n            {\n                maxFrequency = frequencyMap[number];\n                x = number;\n            }\n        }\n\n        return maxFrequency;\n    }\n}\n\nclass Program\n{\n    static void Main()\n    {\n        int[] M = { 4, 5, 6, 4, 4, 3, 5, 3 };\n        int x;\n        int f = MyMath.MostFreq(M, out x);\n        Console.WriteLine("The most frequent item = {0} with frequency = {1}", x, f);\n    }\n}