public class LocalDbTests\n     {\n         private string connectionString = "Server=(localdb)\\mssqllocaldb;Database=TestDb;Trusted_Connection=True;";\n\n         [Fact]\n         public void Test_AddEntityToLocalDb()\n         {\n             using (var context = new MyDbContext(connectionString))\n             {\n                 var entity = new MyEntity { Name = "Test" };\n                 context.MyEntities.Add(entity);\n                 context.SaveChanges();\n\n                 Assert.Equal(1, context.MyEntities.Count());\n             }\n         }\n     }