using System;\nusing System.Collections.Generic;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        var commands = new List<string> { "help", "exit", "list", "add", "remove" };\n        string line = string.Empty;\n        \n        while (line != "exit")\n        {\n            line = ReadLineWithTabCompletion(commands);\n            Console.WriteLine($"You entered: {line}");\n        }\n    }\n\n    static string ReadLineWithTabCompletion(List<string> commands)\n    {\n        ConsoleKeyInfo keyInfo;\n        string input = string.Empty;\n\n        while (true)\n        {\n            keyInfo = Console.ReadKey(intercept: true);\n            \n            if (keyInfo.Key == ConsoleKey.Enter)\n            {\n                Console.WriteLine();\n                return input;\n            }\n            else if (keyInfo.Key == ConsoleKey.Backspace)\n            {\n                if (input.Length > 0)\n                {\n                    input = input.Substring(0, input.Length - 1);\n                    Console.Write("\b \b");\n                }\n            }\n            else if (keyInfo.Key == ConsoleKey.Tab)\n            {\n                string completion = commands.Find(c => c.StartsWith(input));\n                if (completion != null)\n                {\n                    ClearCurrentConsoleLine(input.Length);\n                    input = completion;\n                    Console.Write(input);\n                }\n            }\n            else\n            {\n                input += keyInfo.KeyChar;\n                Console.Write(keyInfo.KeyChar);\n            }\n        }\n    }\n\n    static void ClearCurrentConsoleLine(int length)\n    {\n        for (int i = 0; i < length; i++)\n        {\n            Console.Write("\b \b");\n        }\n    }\n}