using System;\nusing System.Collections.Generic;\nusing System.Linq;\n\npublic class RandomSelector\n{\n    public static List<T> SelectRandomElements<T>(List<T> list, int numberOfElements)\n    {\n        Random random = new Random();\n        return list.OrderBy(x => random.Next()).Take(numberOfElements).ToList();\n    }\n\n    public static void Main()\n    {\n        List<string> myList = new List<string> { "apple", "banana", "cherry", "date", "fig", "grape", "kiwi" };\n        List<string> randomElements = SelectRandomElements(myList, 5);\n\n        foreach (var element in randomElements)\n        {\n            Console.WriteLine(element);\n        }\n    }\n}