using System;\nusing System.IO;\nusing System.Windows.Forms;\n\npublic class ClipboardHelper\n{\n    public static void SetEmbedSourceFromFile(string filePath)\n    {\n        if (!File.Exists(filePath))\n        {\n            throw new FileNotFoundException("The specified file does not exist.", filePath);\n        }\n\n        byte[] fileBytes = File.ReadAllBytes(filePath);\n\n        // Create a MemoryStream from the file bytes\n        using (MemoryStream stream = new MemoryStream(fileBytes))\n        {\n            // Use the DataObject to set the "Embed Source" data\n            DataObject dataObject = new DataObject();\n            dataObject.SetData("Embed Source", stream);\n\n            // Set the data into the clipboard\n            Clipboard.SetDataObject(dataObject, true);\n        }\n    }\n}\n\n// Usage example\nclass Program\n{\n    [STAThread]\n    static void Main()\n    {\n        try\n        {\n            string filePath = @"C:\path\to\your\file";\n            ClipboardHelper.SetEmbedSourceFromFile(filePath);\n            Console.WriteLine("Embed Source data has been successfully set to the clipboard.");\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine("An error occurred: " + ex.Message);\n        }\n    }\n}