using System;\nusing System.IO;\n\npublic class FileCopyUtility\n{\n    public void CopyReadOnlyFile(string sourceFilePath, string destinationDirPath)\n    {\n        FileInfo sourceFile = new FileInfo(sourceFilePath);\n        string destinationFilePath = Path.Combine(destinationDirPath, sourceFile.Name);\n\n        // Remove the read-only attribute from the source file\n        if (sourceFile.IsReadOnly)\n        {\n            sourceFile.IsReadOnly = false;\n        }\n\n        // Copy the file to the destination directory\n        File.Copy(sourceFile.FullName, destinationFilePath, true);\n\n        // Optionally, set the read-only attribute on the copied file\n        FileInfo copiedFile = new FileInfo(destinationFilePath);\n        copiedFile.IsReadOnly = true;\n    }\n}