using System;\nusing Microsoft.Web.Administration;\n\nclass Program\n{\n    static void Main()\n    {\n        string siteName = "Default Web Site";\n        int siteId = GetSiteIdByName(siteName);\n\n        if (siteId != -1)\n        {\n            Console.WriteLine($"The Site ID for '{siteName}' is {siteId}");\n        }\n        else\n        {\n            Console.WriteLine($"Site '{siteName}' not found.");\n        }\n    }\n\n    private static int GetSiteIdByName(string siteName)\n    {\n        using (ServerManager serverManager = new ServerManager())\n        {\n            Site site = serverManager.Sites[siteName];\n            if (site != null)\n            {\n                return (int)site.Id;\n            }\n        }\n        return -1; // Site not found\n    }\n}