public class ExampleUsage\n{\n    public static void Main()\n    {\n        // Example byte array (e.g., result from encryptStringToBytes_AES)\n        byte[] byteArray = { 1, 2, 3, 4, 5 };\n\n        // Convert byte array to string\n        string str = ByteArrayToStringExample.ByteArrayToString(byteArray);\n        Console.WriteLine("String: " + str);\n\n        // Convert string back to byte array\n        byte[] byteArrayConvertedBack = StringToByteArrayExample.StringToByteArray(str);\n        Console.WriteLine("Byte Array: " + BitConverter.ToString(byteArrayConvertedBack));\n    }\n}