using System;\nusing System.Reflection;\n\n[DefaultMember("Item")]\npublic class MyCollection\n{\n    private string[] elements = new string[10];\n\n    // Indexer declaration\n    public string this[int index]\n    {\n        get { return elements[index]; }\n        set { elements[index] = value; }\n    }\n}\n\npublic class Program\n{\n    public static void Main()\n    {\n        MyCollection collection = new MyCollection();\n        collection[0] = "Hello";\n        collection[1] = "World";\n\n        // Accessing the indexer without specifying "Item"\n        Console.WriteLine(collection[0]); // Output: Hello\n        Console.WriteLine(collection[1]); // Output: World\n\n        // Using InvokeMember to access the default member\n        Type type = typeof(MyCollection);\n        object result = type.InvokeMember("Item", BindingFlags.Default | BindingFlags.GetProperty, null, collection, new object[] { 0 });\n        Console.WriteLine(result); // Output: Hello\n    }\n}