using System;\n   using System.Data.SQLite;\n   using System.Linq;\n\n   class Program\n   {\n       static void Main()\n       {\n           string connectionString = "Data Source=your_database_file.db;Version=3;";\n           using (var connection = new SQLiteConnection(connectionString))\n           {\n               connection.Open();\n\n               // Create a table\n               string createTableQuery = "CREATE TABLE IF NOT EXISTS Users (Id INTEGER PRIMARY KEY, Name TEXT)";\n               using (var command = new SQLiteCommand(createTableQuery, connection))\n               {\n                   command.ExecuteNonQuery();\n               }\n\n               // Insert data\n               string insertQuery = "INSERT INTO Users (Name) VALUES (@Name)";\n               using (var command = new SQLiteCommand(insertQuery, connection))\n               {\n                   command.Parameters.AddWithValue("@Name", "John Doe");\n                   command.ExecuteNonQuery();\n               }\n\n               // Query data\n               string selectQuery = "SELECT * FROM Users";\n               using (var command = new SQLiteCommand(selectQuery, connection))\n               using (var reader = command.ExecuteReader())\n               {\n                   while (reader.Read())\n                   {\n                       Console.WriteLine($"Id: {reader["Id"]}, Name: {reader["Name"]}");\n                   }\n               }\n           }\n       }\n   }