using Castle.Components.Validator;\n\npublic class CustomValidationRegistry : IValidationRegistry\n{\n    public void Register()\n    {\n        // Create a validator for your Subsonic generated class\n        var validator = new CachedValidationRegistry();\n\n        // Define validation rules for your Subsonic class properties\n        validator.Register(typeof(YourSubsonicClass), "PropertyName", new NotNullValidator());\n        validator.Register(typeof(YourSubsonicClass), "AnotherPropertyName", new EmailValidator());\n\n        // Register the validator\n        ValidatorRegistry.SetRegistry(validator);\n    }\n}