DbParameter prm = comm.CreateParameter();\nprm.ParameterName = "@Price";\nprm.DbType = DbType.Decimal;\nprm.Direction = ParameterDirection.Output;\n\n// Cast to SqlParameter to access Precision and Scale\nSqlParameter sqlPrm = (SqlParameter)prm;\nsqlPrm.Precision = 18;  // Set as per your required precision\nsqlPrm.Scale = 2;       // Set as per your required scale\n\ncomm.Parameters.Add(prm);\n\n// Execute your command and retrieve the value\ncomm.ExecuteNonQuery();\ndecimal.TryParse(comm.Parameters["@Price"].Value.ToString(), out this.ProdPrice);