using HtmlAgilityPack;\nusing System;\n\nclass Program\n{\n    static void Main()\n    {\n        string input = "<p>Your RSS feed content with <div>tags</div> and other <b>HTML</b> tags.</p>";\n        string output = RemoveDivTags(input);\n        Console.WriteLine(output);\n    }\n\n    static string RemoveDivTags(string input)\n    {\n        HtmlDocument doc = new HtmlDocument();\n        doc.LoadHtml(input);\n\n        // Remove all <div> nodes\n        foreach (var div in doc.DocumentNode.SelectNodes("//div"))\n        {\n            div.Remove();\n        }\n\n        return doc.DocumentNode.InnerHtml;\n    }\n}