using System;\nusing System.Data.SqlClient;\n\nclass Program\n{\n    static void Main()\n    {\n        string connectionString = "your_connection_string";\n        int id = 1; // Your record identifier\n        byte[] newData = new byte[] { 0x10, 0x20, 0x30 }; // Your data to append\n\n        using (SqlConnection connection = new SqlConnection(connectionString))\n        {\n            connection.Open();\n            using (SqlCommand command = connection.CreateCommand())\n            {\n                // Calculate the length of the existing BLOB data\n                command.CommandText = "SELECT DATALENGTH(YourBinaryColumn) FROM YourTable WHERE Id = @Id";\n                command.Parameters.AddWithValue("@Id", id);\n                long offset = (long)command.ExecuteScalar();\n\n                // Update the BLOB by appending new data\n                command.CommandText = "UPDATE YourTable SET YourBinaryColumn .WRITE(@NewData, @Offset, NULL) WHERE Id = @Id";\n                command.Parameters.AddWithValue("@NewData", newData);\n                command.Parameters.AddWithValue("@Offset", offset);\n                command.ExecuteNonQuery();\n            }\n        }\n    }\n}