using System;\nusing System.Text.RegularExpressions;\n\nclass Program\n{\n    static void Main()\n    {\n        string input = @"\n            <a href='http://example.com' title='Example Title'>Example Link</a>\n            <a href='http://example.com'><img src='image.jpg' alt='Example Alt'></a>\n        ";\n\n        string pattern = @"<a\s+(?=[^>]*href=['""](?<url>[^'""]*)['""])(?=[^>]*title=['""](?<title>[^'""]*)['""])?[^>]*>(?:(?:<img\s+(?=[^>]*alt=['""](?<alt>[^'""]*)['""])?[^>]*>)|(?<linkText>[^<]*))</a>";\n\n        Regex regex = new Regex(pattern, RegexOptions.IgnoreCase);\n        MatchCollection matches = regex.Matches(input);\n\n        foreach (Match match in matches)\n        {\n            string url = match.Groups["url"].Value;\n            string title = match.Groups["title"].Value;\n            string alt = match.Groups["alt"].Value;\n            string linkText = match.Groups["linkText"].Value;\n\n            Console.WriteLine($"URL: {url}");\n            Console.WriteLine($"Title: {title}");\n            Console.WriteLine($"Alt Text: {alt}");\n            Console.WriteLine($"Link Text: {linkText}");\n            Console.WriteLine();\n        }\n    }\n}