// Global.asax.cs\nusing System;\nusing System.Data.SqlClient;\nusing System.Web;\n\npublic class Global : HttpApplication\n{\n    protected void Application_Error(object sender, EventArgs e)\n    {\n        Exception exception = Server.GetLastError();\n        \n        // Check if the exception is related to SQL server connection\n        if (exception is SqlException sqlException)\n        {\n            // Log the SQL exception if needed\n            // e.g., log.Error(sqlException);\n\n            // Clear the error to prevent ASP.NET default error handling\n            Server.ClearError();\n\n            // Redirect to the maintenance page\n            Response.Redirect("~/Maintenance.html");\n        }\n    }\n}