public class Program\n{\n    public static void Main(string[] args)\n    {\n        MyClass.InitializationFailed += HandleInitializationFailed;\n\n        MyClass instance = MyClass.GetInstance();\n\n        if (instance.IsInitializedSuccessfully)\n        {\n            // Proceed with using the instance\n        }\n        else\n        {\n            // Handle initialization failure\n        }\n    }\n\n    private static void HandleInitializationFailed(object sender, EventArgs e)\n    {\n        Console.WriteLine("Initialization of MyClass failed.");\n    }\n}