using (SqlConnection connection = new SqlConnection(connectionString))\n{\n    connection.Open();\n    SqlTransaction transaction = connection.BeginTransaction(IsolationLevel.ReadCommitted);\n\n    try\n    {\n        SqlCommand command = new SqlCommand("UPDATE MyTable SET Column = Value WHERE Condition", connection, transaction);\n        command.ExecuteNonQuery();\n\n        // Other commands within the same transaction\n        // ...\n\n        transaction.Commit();\n    }\n    catch (Exception)\n    {\n        transaction.Rollback();\n        throw;\n    }\n}