using System;\nusing System.Web;\nusing System.Web.UI;\n\npublic partial class CreateEmail : Page\n{\n    protected void Page_Load(object sender, EventArgs e)\n    {\n        string to = "recipient@example.com";\n        string subject = HttpUtility.UrlEncode("Your Subject Here");\n        string body = HttpUtility.UrlEncode("Your email body here. This can be HTML if needed.");\n        \n        // Create the mailto link\n        string mailtoLink = $"mailto:{to}?subject={subject}&body={body}";\n        \n        // Register a startup script to open the mailto link in the default mail client\n        string script = $"window.open('{mailtoLink}', '_self');";\n        ClientScript.RegisterStartupScript(this.GetType(), "mailtoScript", script, true);\n    }\n}