using CrystalDecisions.CrystalReports.Engine;\nusing CrystalDecisions.Shared;\n\npublic void SetDatabaseLogon(ReportDocument reportDocument, string serverName, string databaseName, string userId, string password)\n{\n    foreach (Table table in reportDocument.Database.Tables)\n    {\n        TableLogOnInfo logOnInfo = table.LogOnInfo;\n        ConnectionInfo connectionInfo = logOnInfo.ConnectionInfo;\n\n        connectionInfo.ServerName = serverName;\n        connectionInfo.DatabaseName = databaseName;\n        connectionInfo.UserID = userId;\n        connectionInfo.Password = password;\n\n        table.ApplyLogOnInfo(logOnInfo);\n    }\n\n    foreach (ReportDocument subReport in reportDocument.Subreports)\n    {\n        SetDatabaseLogon(subReport, serverName, databaseName, userId, password);\n    }\n}\n\n// Usage example\nReportDocument reportDocument = new ReportDocument();\nreportDocument.Load("path_to_your_report.rpt");\n\nstring serverName = "UAT_DB_SERVER";\nstring databaseName = "UAT_DB_NAME";\nstring userId = "your_user_id";\nstring password = "your_password";\n\nSetDatabaseLogon(reportDocument, serverName, databaseName, userId, password);\n\n// Set the report source of your viewer\nyourCrystalReportViewer.ReportSource = reportDocument;\nyourCrystalReportViewer.Refresh();