using System;\nusing System.Diagnostics;\nusing System.Linq;\n\nclass Program\n{\n    static void Main()\n    {\n        Process p = new Process();\n        p.StartInfo.FileName = "iexplore.exe";\n        p.StartInfo.Arguments = "about:blank";\n        p.Start();\n\n        // Wait for the process to start and the main window to be created\n        p.WaitForInputIdle();\n\n        // Get all instances of Internet Explorer\n        var ieProcesses = Process.GetProcessesByName("iexplore");\n\n        // Find the process that has a main window handle\n        Process ieProcessWithWindow = ieProcesses.FirstOrDefault(proc => proc.MainWindowHandle != IntPtr.Zero);\n\n        if (ieProcessWithWindow != null)\n        {\n            IntPtr mainWindowHandle = ieProcessWithWindow.MainWindowHandle;\n            Console.WriteLine("Main Window Handle: " + mainWindowHandle);\n        }\n        else\n        {\n            Console.WriteLine("No IE process with a main window found.");\n        }\n    }\n}