using System;\nusing System.Runtime.InteropServices;\n\nclass Program\n{\n    [DllImport("kernel32.dll", SetLastError = true)]\n    [return: MarshalAs(UnmanagedType.Bool)]\n    static extern bool AllocConsole();\n\n    static void Main(string[] args)\n    {\n        // Output to the default console\n        Console.WriteLine("This is the default console.");\n\n        // Create and output to a second console\n        if (AllocConsole())\n        {\n            Console.WriteLine("This is the second console.");\n        }\n        else\n        {\n            Console.WriteLine("Failed to create a second console.");\n        }\n\n        // Keep the console windows open\n        Console.ReadLine();\n    }\n}