using System;\nusing Microsoft.Deployment.Compression.Cab;\nusing System.IO;\n\nclass Program\n{\n    static void Main()\n    {\n        string cabFilePath = @"C:\Path\To\Source\archive.cab";\n        string destinationDirectory = @"C:\Path\To\Destination";\n\n        CabInfo cab = new CabInfo(cabFilePath);\n        cab.Unpack(destinationDirectory);\n        \n        Console.WriteLine("CAB file extracted successfully.");\n    }\n}