using System;\nusing Microsoft.Deployment.Compression.Cab;\nusing System.IO;\n\nclass Program\n{\n    static void Main()\n    {\n        string sourceDirectory = @"C:\Path\To\Source\Files";\n        string cabFilePath = @"C:\Path\To\Destination\archive.cab";\n\n        CabInfo cab = new CabInfo(cabFilePath);\n        cab.Pack(sourceDirectory, true, CompressionLevel.Max);\n        \n        Console.WriteLine("CAB file created successfully.");\n    }\n}